import{_ as a,c as e,o as n,e as r}from"./app.4827ea45.js";const g=JSON.parse('{"title":"\u751F\u7269\u5B66","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4E2D\u5FC3\u6CD5\u5219","slug":"\u4E2D\u5FC3\u6CD5\u5219","link":"#\u4E2D\u5FC3\u6CD5\u5219","children":[]},{"level":2,"title":"\u86CB\u767D\u8D28","slug":"\u86CB\u767D\u8D28","link":"#\u86CB\u767D\u8D28","children":[{"level":3,"title":"\u6784\u6210","slug":"\u6784\u6210","link":"#\u6784\u6210","children":[]}]},{"level":2,"title":"\u5BC6\u7801\u5B50","slug":"\u5BC6\u7801\u5B50","link":"#\u5BC6\u7801\u5B50","children":[]},{"level":2,"title":"\u6C28\u57FA\u9178","slug":"\u6C28\u57FA\u9178","link":"#\u6C28\u57FA\u9178","children":[]},{"level":2,"title":"DNA\u5230RNA\u7684\u8F6C\u5F55\u8FC7\u7A0B\u3001RNA\u5230\u6C28\u57FA\u9178\u548C\u86CB\u767D\u8D28\u7684\u7FFB\u8BD1\u8FC7\u7A0B","slug":"dna\u5230rna\u7684\u8F6C\u5F55\u8FC7\u7A0B\u3001rna\u5230\u6C28\u57FA\u9178\u548C\u86CB\u767D\u8D28\u7684\u7FFB\u8BD1\u8FC7\u7A0B","link":"#dna\u5230rna\u7684\u8F6C\u5F55\u8FC7\u7A0B\u3001rna\u5230\u6C28\u57FA\u9178\u548C\u86CB\u767D\u8D28\u7684\u7FFB\u8BD1\u8FC7\u7A0B","children":[]},{"level":2,"title":"DNA\u548CRNA\u7684\u78B1\u57FA\u6784\u6210","slug":"dna\u548Crna\u7684\u78B1\u57FA\u6784\u6210","link":"#dna\u548Crna\u7684\u78B1\u57FA\u6784\u6210","children":[]},{"level":2,"title":"DNA","slug":"dna","link":"#dna","children":[]},{"level":2,"title":"RNA","slug":"rna","link":"#rna","children":[]},{"level":2,"title":"DNA \u548C RNA \u7684\u533A\u522B","slug":"dna-\u548C-rna-\u7684\u533A\u522B","link":"#dna-\u548C-rna-\u7684\u533A\u522B","children":[]}],"relativePath":"live/biology.md","lastUpdated":1679112228000}'),l={name:"live/biology.md"},i=r('<h1 id="\u751F\u7269\u5B66" tabindex="-1">\u751F\u7269\u5B66 <a class="header-anchor" href="#\u751F\u7269\u5B66" aria-hidden="true">#</a></h1><h2 id="\u4E2D\u5FC3\u6CD5\u5219" tabindex="-1">\u4E2D\u5FC3\u6CD5\u5219 <a class="header-anchor" href="#\u4E2D\u5FC3\u6CD5\u5219" aria-hidden="true">#</a></h2><p>\u5B9A\u4E49</p><p>\u4E2D\u5FC3\u6CD5\u5219\u4E5F\u79F0\u4E3A\u5206\u5B50\u751F\u7269\u5B66\u7684\u4E2D\u5FC3\u6559\u6761</p><p>\u5206\u5B50\u751F\u7269\u5B66\u7684\u4E2D\u5FC3\u6CD5\u5219\u65E8\u5728<strong>\u8BE6\u7EC6\u8BF4\u660E\u8FDE\u4E32\u4FE1\u606F\u7684\u9010\u5B57\u4F20\u9001</strong>\uFF0C\u5B83\u6307\u51FA\u9057\u4F20\u4FE1\u606F\u4E0D\u80FD\u7531\u86CB\u767D\u8D28\u8F6C\u79FB\u5230\u86CB\u767D\u8D28\u6216\u6838\u9178\u4E4B\u4E2D</p><p>\u662F\u6307<strong>\u9057\u4F20\u4FE1\u606F\u4ECEDNA\u4F20\u9012\u7ED9RNA</strong>, \u518D<strong>\u4ECERNA\u4F20\u9012\u7ED9\u86CB\u767D\u8D28</strong>\uFF0C\u5373\u5B8C\u6210\u9057\u4F20\u4FE1\u606F\u7684<strong>\u8F6C\u5F55\u548C\u7FFB\u8BD1</strong>\u8FC7\u7A0B</p><p>\u4E5F\u53EF\u4EE5\u4ECEDNA\u4F20\u9012\u7ED9DNA, \u5373\u5B8C\u6210DNA\u7684\u590D\u5236\u8FC7\u7A0B</p><p>\u8FD9\u662F\u6240\u6709<strong>\u6709\u7EC6\u80DE\u7ED3\u6784\u7684\u751F\u7269</strong>\u8981\u9075\u5FAA\u7684\u6CD5\u5219\u3002</p><p>\u603B\u7ED3\uFF1A</p><p>DNA \u5236\u9020 RNA, RNA \u5236\u9020 \u86CB\u767D\u8D28\uFF0C\u86CB\u767D\u8D28\u534F\u4F5C\u524D\u4E24\u9879\u6D41\u7A0B\uFF0C\u5E76\u534F\u52A9DNA\u81EA\u6211\u590D\u5236</p><h2 id="\u86CB\u767D\u8D28" tabindex="-1">\u86CB\u767D\u8D28 <a class="header-anchor" href="#\u86CB\u767D\u8D28" aria-hidden="true">#</a></h2><p>\u86CB\u767D\u8D28\u662F\u7EC4\u6210\u4EBA\u4F53\u4E00\u5207\u7EC6\u80DE\uFF0C\u7EC4\u7EC7\u7684\u91CD\u8981\u6210\u5206\u3002</p><p>\u673A\u4F53\u6240\u6709\u91CD\u8981\u7684\u7EC4\u6210\u90E8\u5206\u90FD\u9700\u8981\u86CB\u767D\u8D28\u7684\u53C2\u4E0E\u3002</p><p>\u86CB\u767D\u8D28\u662F\u751F\u547D\u7684\u7269\u8D28\u57FA\u7840\uFF0C\u662F\u6709\u673A\u5927\u5206\u5B50\uFF0C\u662F\u6784\u6210\u7EC6\u80DE\u7684\u57FA\u672C\u6709\u673A\u7269\uFF0C\u662F\u751F\u547D\u6D3B\u52A8\u7684\u4E3B\u8981\u627F\u62C5\u8005\u3002</p><p><strong>\u6C28\u57FA\u9178</strong>\u662F\u86CB\u767D\u8D28\u7684\u57FA\u672C\u7EC4\u6210\u5355\u4F4D\u3002</p><p>\u4EBA\u4F53\u5FC5\u9700\u7684\u4E03\u5927\u8425\u517B\u7D20\uFF1A</p><p>\u86CB\u767D\u8D28\uFF0C\u7EF4\u751F\u7D20\uFF0C\u78B3\u6C34\u5316\u5408\u7269\uFF0C\u77FF\u7269\u8D28\uFF0C\u6C34\uFF0C\u81B3\u98DF\u7EA4\u7EF4\uFF0C\u8102\u7C7B</p><h3 id="\u6784\u6210" tabindex="-1">\u6784\u6210 <a class="header-anchor" href="#\u6784\u6210" aria-hidden="true">#</a></h3><p>\u86CB\u767D\u8D28\u7ED3\u6784\u662F\u6307\u86CB\u767D\u8D28\u5206\u5B50\u7684\u7A7A\u95F4\u7ED3\u6784</p><p>\u86CB\u767D\u8D28\u4E3B\u8981\u662F\u7531 \u78B3\uFF0C\u6C22\uFF0C \u6C27\uFF0C \u6C2E \u7B49\u5316\u5B66\u5143\u7D20\u7EC4\u6210\uFF0C\u662F\u4E00\u7C7B\u91CD\u8981\u7684\u751F\u7269\u5927\u5206\u5B50</p><p>\u6240\u6709\u86CB\u767D\u8D28\u90FD\u662F\u753120\u79CD\u4E0D\u540C\u7684\u6C28\u57FA\u9178\u8FDE\u7ED3\u5F62\u6210\u7684\u591A\u805A\u4F53</p><h2 id="\u5BC6\u7801\u5B50" tabindex="-1">\u5BC6\u7801\u5B50 <a class="header-anchor" href="#\u5BC6\u7801\u5B50" aria-hidden="true">#</a></h2><p>codeon\u662F\u6307 \u4FE1\u4F7FRNA\u5206\u5B50\u4E2D\u6BCF\u76F8\u90BB\u76843\u4E2A\u6838\u82F7\u9178\u7F16\u6210\u4E00\u7EC4\uFF0C\u5728\u86CB\u767D\u8D28\u5408\u6210\u65F6\u4EE3\u8868\u4E00\u79CD\u6C28\u57FA\u9178\u7684\u89C4\u5F8B</p><p>\u4FE1\u4F7FRNA\u5728\u7EC6\u80DE\u4E2D\u80FD\u51B3\u5B9A\u86CB\u767D\u8D28\u5206\u5B50\u4E2D\u7684\u6C28\u57FA\u9178\u79CD\u7C7B\u548C\u6392\u5217\u6B21\u5E8F</p><h2 id="\u6C28\u57FA\u9178" tabindex="-1">\u6C28\u57FA\u9178 <a class="header-anchor" href="#\u6C28\u57FA\u9178" aria-hidden="true">#</a></h2><p>\u6C28\u57FA\u9178 \u662F\u542B\u6709 \u78B1\u6027\u6C28\u57FA \u548C \u9178\u6027 \u7FA7(suo)\u57FA \u7684\u6709\u673A\u5316\u5408\u7269</p><h2 id="dna\u5230rna\u7684\u8F6C\u5F55\u8FC7\u7A0B\u3001rna\u5230\u6C28\u57FA\u9178\u548C\u86CB\u767D\u8D28\u7684\u7FFB\u8BD1\u8FC7\u7A0B" tabindex="-1">DNA\u5230RNA\u7684\u8F6C\u5F55\u8FC7\u7A0B\u3001RNA\u5230\u6C28\u57FA\u9178\u548C\u86CB\u767D\u8D28\u7684\u7FFB\u8BD1\u8FC7\u7A0B <a class="header-anchor" href="#dna\u5230rna\u7684\u8F6C\u5F55\u8FC7\u7A0B\u3001rna\u5230\u6C28\u57FA\u9178\u548C\u86CB\u767D\u8D28\u7684\u7FFB\u8BD1\u8FC7\u7A0B" aria-hidden="true">#</a></h2><p><strong>DNA\u7684\u590D\u5236</strong>\uFF1A</p><p>DNA\u53CC\u94FE\u6253\u5F00(\u89E3\u65CB)\uFF0C\u5404\u81EA\u4F5C\u4E3A\u6A21\u677F\uFF0C\u4EA7\u751F\u53E6\u4E00\u6761\u4E92\u8865\u7684DNA\u65B0\u94FE</p><p>\u6700\u7EC8\u5F97\u5230\u4E24\u4E2A\u5B8C\u5168\u4E00\u6837\u7684DNA\u53CC\u94FE\uFF0C\u4E3A\u63A5\u4E0B\u6765\u7684\u7EC6\u80DE\u5206\u88C2\u505A\u51C6\u5907</p><p>DNA\u7684\u590D\u5236\u662F\u534A\u4FDD\u7559\u7684\uFF0C\u56E0\u4E3A\u65B0\u7684DNA\u5355\u94FE\u662F\u6839\u636E\u65E7\u7684DNA\u5355\u94FE\u201C\u590D\u5236\u201D\u800C\u6765\u7684\uFF0C\u6BCF\u4E00\u4E2A\u65B0\u7684\u53CC\u94FE\u91CC\u9762\u90FD\u6709\u4E00\u4E2A\u65E7\u94FE\u548C\u4E00\u4E2A\u65B0\u94FE\u3002</p><p><strong>DNA\u7684\u8F6C\u5F55</strong>\uFF1A</p><p>\u6839\u636EDNA (\u4F5C\u4E3A\u6A21\u677F\u94FE) \u5236\u9020RNA</p><p>\u4F46\u4E0D\u662F\u6240\u6709\u7684\u8F6C\u5F55\u7684RNA\u90FD\u4F1A\u7528\u4F5C\u5236\u9020\u86CB\u767D\u8D28\uFF0C\u4E5F\u4E0D\u662F\u6240\u6709\u7684DNA\u90FD\u4F1A\u88AB\u8F6C\u5F55</p><p>\u65E0\u6838\u7EC6\u80DE\u7684\u57FA\u56E0\u5927\u90E8\u5206\u4F1A\u88AB\u8F6C\u5F55\uFF0C\u4F46\u6709\u6838\u7EC6\u80DE\u7279\u522B\u50CF\u4EBA\u8FD9\u79CD\u590D\u6742\u7684\u751F\u7269\u53EA\u6709\u4E0D\u52301.5%\u7684\u4F1A\u88AB\u8F6C\u5F55</p><p><strong>DNA\u7684\u7FFB\u8BD1</strong>\uFF1A</p><p>\u6839\u636EmRNA\u5236\u9020\u591A\u80BD\u94FE \u86CB\u767D\u8D28</p><p>mRNA\u4E0A\u7684\u6BCF\u4E2A\u78B1\u57FA\u6210\u4E3A\u4E00\u4E2A\u5BC6\u7801\u5B50</p><p>\u4E00\u4E2A\u5BC6\u7801\u5B50\u4F1A\u5BF9\u5E94\u4E00\u4E2A\u6C28\u57FA\u9178 \u6216\u8005 \u4E00\u4E2A\u7EC8\u6B62\u5BC6\u7801\u5B50</p><h2 id="dna\u548Crna\u7684\u78B1\u57FA\u6784\u6210" tabindex="-1">DNA\u548CRNA\u7684\u78B1\u57FA\u6784\u6210 <a class="header-anchor" href="#dna\u548Crna\u7684\u78B1\u57FA\u6784\u6210" aria-hidden="true">#</a></h2><p>DNA\u548CRNA\u90FD\u662F\u7531C,H,O,N,P\u6784\u6210 N\u4E3A\u542B\u6C2E\u78B1\u57FA\u91CC P\u4E3A\u78F7\u9178\u91CC</p><p>DNA\u78B1\u57FA\uFF1A</p><ol><li>A\u817A\u560C\u5464</li><li>G\u9E1F\u560C\u5464</li><li>C\u80DE\u5627\u5576</li><li>T\u80F8\u817A\u5627\u5576</li></ol><p>RNA\u78B1\u57FA\uFF1A</p><ol><li>A\u817A\u560C\u5464</li><li>G\u9E1F\u560C\u5464</li><li>C\u80DE\u5627\u5576</li><li>U\u5C3F\u5627\u5576</li></ol><h2 id="dna" tabindex="-1">DNA <a class="header-anchor" href="#dna" aria-hidden="true">#</a></h2><p>\u8131\u6C27\u6838\u7CD6\u6838\u9178</p><h2 id="rna" tabindex="-1">RNA <a class="header-anchor" href="#rna" aria-hidden="true">#</a></h2><p>\u6838\u7CD6\u6838\u9178</p><h2 id="dna-\u548C-rna-\u7684\u533A\u522B" tabindex="-1">DNA \u548C RNA \u7684\u533A\u522B <a class="header-anchor" href="#dna-\u548C-rna-\u7684\u533A\u522B" aria-hidden="true">#</a></h2><p><strong>DNA\u548CRNA\u6240\u6307\u4EE3\u7684\u7269\u8D28\u4E0D\u540C</strong>:</p><ol><li>DNA\uFF0C\u5373\u8131\u6C27\u6838\u7CD6\u6838\u9178\uFF0C\u5176\u57FA\u672C\u7EC4\u6210\u5355\u4F4D\u662F\u8131\u6C27\u6838\u82F7\u9178\uFF1B</li><li>RNA\uFF0C\u5373\u6838\u7CD6\u6838\u9178\uFF0C\u5176\u57FA\u672C\u7EC4\u6210\u5355\u4F4D\u662F\u6838\u7CD6\u6838\u82F7\u9178</li></ol><p><strong>\u7A7A\u95F4\u7ED3\u6784\u4E0D\u540C</strong>:</p><ol><li>DNA\u5206\u5B50\u6709\u4E24\u4E2A\u94FE\u6761\uFF0C\u7EBF\u5708\u5468\u56F4\u5F62\u6210\u53CC\u87BA\u65CB\uFF0C\u643A\u5E26\u9057\u4F20\u6307\u4EE4\uFF0C\u7528\u4E8E\u751F\u957F\u3001\u53D1\u80B2\u548C\u751F\u7269\u3001\u75C5\u6BD2\u7684\u7E41\u6B96</li><li>RNA\u901A\u5E38\u662F\u5355\u94FE\u6298\u53E0\u800C\u6210\uFF0C\u5E76\u4E0D\u662F\u6210\u5BF9\u7684\u53CC\u94FE</li><li>DNA\u53CC\u94FE\u7ED3\u6784\u6BD4RNA\u5355\u94FE\u7ED3\u6784\u66F4\u5177\u6709\u7A33\u5B9A\u6027\uFF0C\u5C24\u5176\u8FDB\u884C\u81EA\u6211\u590D\u5236\u65F6\uFF0CDNA\u53CC\u94FE\u7A33\u5B9A\u7ED3\u6784\u53EF\u4EE5\u5C3D\u53EF\u80FD\u4FDD\u8BC1\u4E0D\u88AB\u5916\u6765\u57FA\u56E0\u788E\u7247\u66FF\u6362\uFF0C\u4ECE\u800C\u7EF4\u6301\u751F\u7269\u57FA\u56E0\u591A\u6837\u6027</li><li>RNA\u5355\u94FE\u7ED3\u6784\u5728\u590D\u5236\u8FC7\u7A0B\u4E2D\u7A33\u5B9A\u6027\u8F83\u5DEE\uFF1B</li></ol><p><strong>\u529F\u80FD\u4E0D\u540C</strong>\uFF1A</p><p>\u7EC6\u80DE\u7ED3\u6784\u751F\u7269\u4E2D\u90FD\u542B\u6709DNA\u548CRNA\u4E24\u79CD\u6838\u9178\uFF0C\u4F46\u662F\u5F53DNA\u548CRNA\u540C\u65F6\u5B58\u5728\u65F6\uFF0CDNA\u624D\u662F\u9057\u4F20\u7269\u8D28\uFF0C\u6240\u4EE5\u771F\u6838\u751F\u7269\u548C\u539F\u6838\u751F\u7269\u7684\u9057\u4F20\u7269\u8D28\u5747\u4E3ADNA\uFF0C\u800CRNA\u4E00\u822C\u5728\u751F\u7269\u7EC6\u80DE\u4E2D\u4E0D\u4F5C\u4E3A\u9057\u4F20\u7269\u8D28\u3002</p>',56),t=[i];function d(p,h,s,o,A,N){return n(),e("div",null,t)}const D=a(l,[["render",d]]);export{g as __pageData,D as default};
