import{_ as e,c as o,o as s,e as t}from"./app.19198b8f.js";const n="/zwd/assets/1.32984bab.png",u=JSON.parse('{"title":"\u9274\u6743\u7684\u53D1\u5C55\u5386\u7A0B","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u80CC\u666F","slug":"\u80CC\u666F","link":"#\u80CC\u666F","children":[]},{"level":2,"title":"cookie","slug":"cookie","link":"#cookie","children":[]},{"level":2,"title":"session","slug":"session","link":"#session","children":[]},{"level":2,"title":"token","slug":"token","link":"#token","children":[]}],"relativePath":"work/authentication.md","lastUpdated":1678419655000}'),i={name:"work/authentication.md"},a=t('<h1 id="\u9274\u6743\u7684\u53D1\u5C55\u5386\u7A0B" tabindex="-1">\u9274\u6743\u7684\u53D1\u5C55\u5386\u7A0B <a class="header-anchor" href="#\u9274\u6743\u7684\u53D1\u5C55\u5386\u7A0B" aria-hidden="true">#</a></h1><h2 id="\u80CC\u666F" tabindex="-1">\u80CC\u666F <a class="header-anchor" href="#\u80CC\u666F" aria-hidden="true">#</a></h2><p>\u6807\u51C6\u7684Http\u534F\u8BAE\u662F\u65E0\u72B6\u6001\u7684\uFF0C\u6B63\u56E0\u4E3A\u8FD9\u79CD\u65E0\u72B6\u6001\u7684\u7F18\u6545\uFF0C\u5BFC\u81F4\u50CF\u8FD9\u6837\u7684\u573A\u666F\u4E0B\u7528\u6237\u4F53\u9A8C\u5C31\u5F88\u4E0D\u597D\u3002</p><p>\u4F8B\u5982\uFF0C\u4E00\u4E2A\u7F51\u7AD9\uFF0C\u5B83\u6709\u4E00\u4E2A\u9996\u9875\u6709\u4E00\u4E2A\u6B22\u8FCE\u9875\u9762\uFF0C\u5F53\u9996\u6B21\u8BBF\u95EE\u5B83\u7684\u65F6\u5019\u5148\u5B9A\u5411\u5230\u6B22\u8FCE\u9875\u9762\u518D\u8FDB\u5165\u9996\u9875\uFF0C\u800C\u5728\u4E00\u4E2A\u6709\u6548\u65F6\u95F4\u5185\u91CD\u590D\u8BBF\u95EE\u7684\u8BDD\u5C31\u53EF\u4EE5\u8DF3\u8FC7\u6B22\u8FCE\u9875\u9762\u76F4\u63A5\u8FDB\u884C\u9996\u9875\u3002</p><p>\u4F46\u662F\u7531\u4E8E\u7F51\u7EDC\u901A\u8BAF\u662F\u65E0\u72B6\u6001\u7684\uFF0C\u8FD9\u6837\u7684\u9700\u6C42\u662F\u65E0\u6CD5\u5B9E\u73B0\u7684\u3002</p><p>\u56E0\u4E3A\u53EA\u8981\u53D1\u8D77\u7684\u8BF7\u6C42\u662F\u4E00\u6837\u7684\uFF0C\u670D\u52A1\u5668\u662F\u65E0\u6CD5\u77E5\u9053\u54EA\u4E00\u4E2A\u7528\u6237\u4ECE\u54EA\u4E00\u4E2A\u5730\u65B9\u8FDB\u884C\u8BBF\u95EE\uFF0C\u6240\u5F97\u5230\u7684\u54CD\u5E94\u7ED3\u679C\u90FD\u662F\u4E00\u6837\u7684\u3002</p><p>\u8FD9\u5C31\u662F\u6700\u65E9\u7684\u6807\u51C6Http\u534F\u8BAE\u3002</p><h2 id="cookie" tabindex="-1">cookie <a class="header-anchor" href="#cookie" aria-hidden="true">#</a></h2><p>\u76F8\u5BF9\u4E8E\u6B63\u9910\u662F\u9080\u8BF7\u6C42\u7684\u6587\u4EF6\uFF0Ccookie\u53EA\u662F\u643A\u5E26\u7684\u5C0F\u96F6\u98DF\u3002</p><p>\u53D1\u8D77\u7F51\u7EDC\u8BF7\u6C42\u540E\uFF0C\u670D\u52A1\u5668\u8FD4\u56DE\u7684\u54CD\u5E94\u5934RES\u91CC\u643A\u5E26\u4E86cookie\uFF0C<code>set-cookie: key=value</code>\uFF0C\u8FD9\u91CC\u9644\u52A0\u7684\u662F\u72B6\u6001\u4FE1\u606F\u3002</p><p>\u8FD9\u6837\u5939\u5E26\u79C1\u8D27\u7684\u662F\u5728\u6807\u51C6Http\u534F\u8BAE\u91CC\u89C4\u5B9A\u7684\uFF0C\u4E5F\u5C31\u662F\u8BF4\u4EFB\u4F55\u6807\u51C6\u6D4F\u89C8\u5668\u90FD\u4F1A\u652F\u6301\u8FD9\u6837\u4E00\u4E2A\u529F\u80FD\u3002</p><p>\u5F53\u670D\u52A1\u5668\u8FD4\u56DE\u8FC7\u7684\u54CD\u5E94\u5934\u91CC\u6709cookie\u4E4B\u540E\uFF0C\u540E\u7EED\u6D4F\u89C8\u5668\u518D\u6B21<strong>\u53D1\u9001\u8BF7\u6C42\u65F6\u5C31\u4F1A\u643A\u5E26\u8FD9\u4E2Acookie</strong>\uFF0C\u8FD4\u56DE\u7ED9\u670D\u52A1\u5668\uFF0C\u670D\u52A1\u5668\u4E00\u770B\u8FD9\u4E2A\u8BF7\u6C42\u91CC\u6709\u6211\u4E4B\u524D\u8BBE\u7F6E\u7684cookie\uFF0C\u90A3\u5C31\u80FD\u8BC6\u522B\u51FA\u8FD9\u662F\u91CD\u590D\u7684\u8BF7\u6C42\u3002 \u670D\u52A1\u5668\u9700\u8981\u66F4\u65B0\u8FD9\u4E2A\u72B6\u6001\u65F6\uFF0C\u4F1A\u5728\u540E\u7EED\u7684\u8BF7\u6C42\u4E2D\u76F8\u5E94\u7684\u4FEE\u6539cookie\u3002</p><p>\u4F46\u8FD9\u6837\u4F1A\u6709\u4E2A\u95EE\u9898\uFF0Ccookie\u5DF2\u7ECF\u662F\u4E00\u79CD\u6807\u51C6\u534F\u8BAE\u89C4\u5B9A\u7684\u5B58\u5728\uFF0C\u90A3\u6240\u6709\u53D1\u9001\u7684\u8BF7\u6C42\u91CC\u7684cookie\u90FD\u662F\u5728\u63A7\u5236\u53F0\u4E2D\u53EF\u4EE5\u76F4\u63A5\u770B\u5230\u7684\uFF0C\u8FD9\u6837\u6570\u636E\u7684<strong>\u5B89\u5168\u6027\u65E0\u6CD5\u4FDD\u8BC1</strong>\u3002</p><p>\u6B63\u56E0\u4E3Acookie\u5B58\u5728\u8FD9\u6837\u7684\u98CE\u9669\uFF0C\u4E00\u4E9B\u4F01\u4E1A\u7684\u5B89\u5168\u7B56\u7565\u4F1A\u628Acookie\u7981\u7528\uFF0C\u8FD9\u6837\u5982\u679C\u5B9E\u73B0\u7684\u529F\u80FD\u662F\u4F9D\u8D56cookie\u7684\uFF0C\u8FD9\u65F6\u5019\u5C31\u65E0\u6CD5\u751F\u6548\u3002</p><p><img src="'+n+'" alt="authentication"></p><h2 id="session" tabindex="-1">session <a class="header-anchor" href="#session" aria-hidden="true">#</a></h2><p>\u56E0\u4E3Acookie\u8FD9\u6837\u660E\u6587\u4F20\u8F93\uFF0C\u4F1A\u6CC4\u9732\u540E\u53F0\u7684\u4E00\u4E9B\u72B6\u6001\u7684\u5B89\u5168\u6027\u95EE\u9898\uFF0Csession\u51FA\u73B0\u3002</p><p>session\u5C31\u662F\u4E00\u4E2A\u4F1A\u8BDD\u3002</p><p>\u5C06\u6240\u6709\u7684\u72B6\u6001\u4FE1\u606F\u7531\u670D\u52A1\u7AEF\u6765\u4FDD\u5B58\uFF0C\u4F1A\u7ED9\u8FD9\u4E2Asession\u72B6\u6001\u7684\u4E0A\u4E0B\u6587\u8BBE\u7F6E\u4E00\u4E2AID\uFF0C\u90A3\u6D4F\u89C8\u5668\u7AEF\u53EA\u4F1A\u4FDD\u5B58\u4E00\u4E2A<strong>session ID</strong>\uFF0C\u800C\u4E0D\u662F\u660E\u6587\u7684key=value\uFF0C\u8FD9\u6837\u5C31\u907F\u514D\u7684\u4FE1\u606F\u6CC4\u9732\u7684\u9690\u60A3\u3002</p><p>\u4F46\u662Fsession\u4E5F\u5B58\u5728\u4E00\u4E2A\u95EE\u9898\uFF0C\u5982\u679C\u670D\u52A1\u662F\u5206\u5E03\u5F0F\u7684\uFF0C\u9996\u6B21\u8BF7\u6C42\u7684\u670D\u52A1\u5668\u662FA\u5730\u7684\u670D\u52A1\u5668\uFF0C\u8FD9\u4E2Asession\u4FDD\u5B58\u5728A\uFF0C\u7531A\u751F\u6210\u7684session ID \u8FD4\u56DE\u7ED9\u6D4F\u89C8\u5668\u7AEF\u3002</p><p>\u5F53\u4E0B\u6B21\u8BF7\u6C42\u7684\u65F6\u5019\u5982\u679CA\u670D\u52A1\u5668\u6B63\u5FD9\uFF0C\u7531B\u670D\u52A1\u5668\u54CD\u5E94\uFF0C\u4F46\u662FB\u4E2D\u6CA1\u6709\u8FD9\u4E2Asession ID\u5BF9\u5E94\u7684\u4E0A\u4E0B\u6587\u6570\u636E\uFF0C\u5B83\u8FD8\u9700\u8981\u53BBA\u4E2D\u67E5\u8BE2\uFF0C\u8FD9\u6837\u5C31<strong>\u589E\u52A0\u6027\u80FD\u5F00\u9500</strong>\u3002</p><p>\u65E0\u8BBA\u662Fcookie\u8FD8\u662Fsession\u90FD\u662F\u7531\u670D\u52A1\u7AEF\u6765\u4E3B\u5BFC\uFF0C\u524D\u7AEF\u4F9D\u9760\u6807\u51C6\u6D4F\u89C8\u5668\u6765\u5B9E\u73B0\u3002</p><h2 id="token" tabindex="-1">token <a class="header-anchor" href="#token" aria-hidden="true">#</a></h2><p>token\u51FA\u73B0\u7684\u80CC\u666F\u662F\u524D\u7AEF\u65E5\u76CA\u53D1\u5C55\uFF0C\u524D\u540E\u7AEF\u5206\u79BB\uFF0C\u6570\u636E\u4EA4\u4E92\u683C\u5F0F\u4F7F\u7528json\u3002</p><p>\u540E\u7AEF\u628A\u9700\u8981\u4FDD\u5B58\u7684\u6570\u636E<strong>\u4E0D\u4FDD\u5B58</strong>\u5728\u670D\u52A1\u5668\uFF0C\u5C06\u9700\u8981\u7684\u8FD9\u4E9B\u4E0A\u4E0B\u6587\u7684\u72B6\u6001\u6253\u5305\u6210\u4E00\u4E2Atoken\uFF0C\u5BF9\u8FD9\u4E2A<strong>token\u52A0\u5BC6</strong>\u3002</p><p>token\u5C31\u662F\u4E2A\u4EE4\u724C\uFF0C<strong>\u6240\u6709\u4FE1\u606F\u90FD\u5C01\u88C5\u5230token\u91CC</strong>\uFF0C\u670D\u52A1\u5668\u7AEF\u53EA\u8BA4token\uFF0C\u8FD9\u4E2Atoken\u4EA4\u7ED9\u524D\u7AEF\uFF0C\u7531\u4E8E\u8FD9\u4E2Atoken\u662F\u52A0\u5BC6\u7684\uFF0C\u524D\u7AEF\u4E00\u822C\u65E0\u6CD5\u8BC6\u522B\u5176\u4E2D\u5185\u5BB9\uFF0C\u8FD9\u6837\u5C31\u89E3\u51B3\u4E86cookie\u8FD9\u6837\u7684\u660E\u6587\u5C55\u793A\u7684 \u5B89\u5168\u6027\u95EE\u9898\u3002</p><p>\u524D\u7AEF\u5728\u9700\u8981\u7684\u65F6\u5019\u628A\u8FD9\u4E2Atoken\u8FDE\u540C\u8BF7\u6C42\u4E00\u8D77\u8FD4\u56DE\u7ED9\u540E\u7AEF\uFF0C\u90A3\u540E\u7AEF\u83B7\u53D6\u5230\u8FD9\u4E2Atoken\u4E4B\u540E\u5C31\u53EF\u4EE5\u5BF9\u5176<strong>\u89E3\u5305</strong>\uFF0C\u5C31\u80FD\u83B7\u53D6\u5230\u5176\u4E2D\u7684\u4FE1\u606F\u5185\u5BB9\u3002\u90A3\u5BF9\u4E8E\u5206\u5E03\u5F0F\u591A\u5730\u90E8\u7F72\u7684\u60C5\u51B5\uFF0C\u670D\u52A1\u5668\u7AEF\u4E5F\u4E0D\u4F1A\u76F8\u4E92\u53BB\u8BF7\u6C42\uFF0C\u56E0\u4E3A\u6240\u6709\u9700\u8981\u7684\u4FE1\u606F\u6570\u636E\u90FD\u5DF2\u7ECF\u6253\u5305\u5230token\u91CC\uFF0C\u8FD9\u6837\u4E5F\u5C31\u89E3\u51B3\u4E86session\u8FD9\u79CD\u65B9\u6848\u7684\u6027\u80FD\u5F00\u9500\u7684\u95EE\u9898\u3002</p><p>token\u4E0D\u662F\u6807\u51C6\u534F\u8BAE\u89C4\u5B9A\u7684\u4E00\u90E8\u5206\uFF0C\u5B83\u662F\u7531\u524D\u7AEF\u540E\u6765\u534F\u5546\u5B9A\u4E49\u7684\u3002</p>',28),r=[a];function p(c,k,d,h,l,_){return s(),o("div",null,r)}const f=e(i,[["render",p]]);export{u as __pageData,f as default};
